import React, { useEffect, useState } from 'react'

let count = 0
let countId = 100

const Menu = () => {

const [active, setActive] = useState(false)
const [hover, setHover] = useState(0)

function dropDisplay(){
  if(hover >= 1){
    setActive(true)
  } else {
    setActive(false)
  }

  setHover(0)
}

useEffect(()=>{
  document.getElementById('Pricing').addEventListener('mouseover', dropDisplay)
},
[dropDisplay])

  return (
    <div>
      <ul className='flex justify-center text-white text-[22px]'>
        {
            menu.map(item => {
                return (
                    <li key={count++} className='px-4 py-[15px] hover:cursor-pointer' id={item.title}>
                        <a href={item.link}>
                            {item.title}
                        </a>
                    </li>
                )
            })
        }
      </ul>
      <div className={`dropdown absolute top-[80px] left-[465px] w-64 ${active ? 'hidden' : 'block'}`} id='dropdown'>
        <ul>
          {
            menu[2].product.map(item => {
              return (
                <li 
                key={countId++} 
                className='text-[22px] pl-5 w-full py-[15px] hover:cursor-pointer bg-blue-500 hover:bg-blue-800 text-white transition-all '
                >
                  <a href={item.link} className='pr-20 py-2'>
                    {item.stock}
                  </a>
                </li>
              )
            })
          }
        </ul>
      </div>
    </div>
  )
}

export default Menu

export const menu = [
    {
            title: 'Home', 
            link: '#',
            dropdown: false
    },
    {
            title: 'About', 
            link: '#',
            dropdown: false
    },
    {
            title: 'Pricing', 
            link: '#',
            dropdown: true,
            product: [
                {stock: 'Shoes', link: '#'},
                {stock: 'Watches', link: '#'},
                {stock: 'Tuxedos', link: '#'},
                {stock: 'Rings', link: '#'},
            ]
    },
    {
            title: 'Contact', 
            link: '#',
            dropdown: false
        }
]
