import React, { useState } from 'react'

let count = 0

const Menu = () => {

    const [mouseEnter, setMouseEnter] = useState('false')

    function handleMouseEnter(){
        setMouseEnter(
            mouseEnter ? 'true' : 'false'
        )
        console.log(mouseEnter)
    }

  return (
    <div>
      <ul className='flex justify-center text-white text-[22px]'>
        {
            menu.map(item => {
                return (
                    <li key={count++} className='px-4 hover:cursor-pointer' onMouseEnter={handleMouseEnter}>
                        <a href={item.link}>
                            {item.title}
                        </a>
                    </li>
                )
            })
        }
      </ul>
      <p>{mouseEnter}</p>
    </div>
  )
}

export default Menu

const menu = [
    {
            title: 'Home', 
            link: '#',
            dropdown: false
    },
    {
            title: 'About', 
            link: '#',
            dropdown: false
    },
    {
            title: 'Pricing', 
            link: '#',
            dropdown: true,
            product: [
                {stock: 'Shoes', link: '#'},
                {stock: 'Watches', link: '#'},
                {stock: 'Tuxedos', link: '#'},
                {stock: 'Rings', link: '#'},
            ]
    },
    {
            title: 'Contact', 
            link: '#',
            dropdown: false
        }
]
