import React, { useState } from 'react'

const Theme = () => {

    const [toggle, setToggle] = useState(false)

    function handleClick(){
        setToggle(!toggle)
        console.log(toggle)
    }

  return (
    <>
        {
            toggle === false ? (
                <button className="bg-purple-500 text-white text-2xl w-20 h-20 -skew-x-[20deg] shadow-around" onClick={handleClick}><div className="sun"></div></button>
            ) :
            (
                <button className="bg-blue-950 text-white text-2xl w-20 h-20 skew-x-[20deg] shadow-around" onClick={handleClick}>Off</button>
            )
        }
    </>
  )
}

export default Theme
