import React from 'react'
import Menu from './Menu'
import Search from './Search'
import Theme from './Theme'
import { useState } from 'react'
import { useEffect } from 'react'

const Navigation = () => {

    const [show, setShow] = useState(true)
    const [lastScrollY, setLastScrollY] = useState(0)

    function handleNav(){
        if(window.scrollY > lastScrollY) {
            setShow(false)
        } else {
            setShow(true)
        }

        setLastScrollY(window.scrollY)
        // console.log(lastScrollY)
    }

    useEffect(()=>{
        window.addEventListener('scroll', handleNav)

        return () => window.addEventListener('scroll', handleNav)
    },[handleNav])

  return (
    <>
        <nav className={`fixed w-full h-16 shadow-lg px-8 py-5 flex justify-between items-center ${show ? 'top-0' : '-top-10'} transition-all`}>
        <h1 className='text-3xl uppercase font-black text-white'>T&M</h1>
        <Menu/>
        <Search/>
        <Theme/>
      </nav>
    </>
  )
}

export default Navigation
