import React from 'react'

let count = 0

const Menu = () => {

  return (
    <div>
      <ul className='flex justify-center text-white text-[22px]'>
        {
            menu.map(item => {
                return (
                    <li key={count++} className='px-4'>
                        <a href={item.link}>
                            {
                                item.dropdown === true ?
                                (
                                    <ul>
                                        {
                                            item[2].product.map(list => {
                                                <li key={count++}>
                                                    <a href={list.link}>
                                                        {list.item}
                                                    </a>
                                                </li>
                                            })
                                        }
                                    </ul>
                                ) : null
                            }
                        </a>
                    </li>
                )
            })
        }
      </ul>
    </div>
  )
}

export default Menu

const menu = [
    {
            title: 'Home', 
            link: '#',
            dropdown: false
    },
    {
            title: 'About', 
            link: '#',
            dropdown: false
    },
    {
            title: 'Pricing', 
            link: '#',
            dropdown: true,
            product: [
                {item: 'Shoes', link: '#'},
                {item: 'Watches', link: '#'},
                {item: 'Tuxedos', link: '#'},
                {item: 'Rings', link: '#'},
            ]
    },
    {
            title: 'Contact', 
            link: '#',
            dropdown: false
        }
]
