import React, { useEffect, useState } from 'react'

let count = 0
let countId = 100

const Menu = () => {

  const [isDropdownVisible, setIsDropdownVisible] = useState(false)

  function handleMouseEnter(){
    setIsDropdownVisible(true)
  }

  function handleMouseLeave(){
    setIsDropdownVisible(false)
  }

  return (
    <div>
      <ul className='flex justify-center text-white text-[22px]'>
        {
            menu.map(item => {
                return (
                    <li key={count++} className='px-4 py-[15px] hover:cursor-pointer' id={item.title}>
                        <a href={item.link}>
                            {item.title}
                        </a>
                    </li>
                )
            })
        }
      </ul>
      <div 
      className="dropdown absolute top-[80px] left-[465px] w-64"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      >

      </div>
    </div>
  )
}

export default Menu

export const menu = [
    {
            title: 'Home', 
            link: '#',
            dropdown: false
    },
    {
            title: 'About', 
            link: '#',
            dropdown: false
    },
    {
            title: 'Pricing', 
            link: '#',
            dropdown: true,
            product: [
                {stock: 'Shoes', link: '#'},
                {stock: 'Watches', link: '#'},
                {stock: 'Tuxedos', link: '#'},
                {stock: 'Rings', link: '#'},
            ]
    },
    {
            title: 'Contact', 
            link: '#',
            dropdown: false
        }
]
