        {
            menu.map(item => {
                return (
                    <li key={count++} className='px-4 py-[15px] hover:cursor-pointer' id={item.title}>
                        <a href={item.link}>
                            {item.title}
                        </a>
                    </li>
                )
            })
        }
      </ul>
      <div 
